<?phpclass PageController extends Controller {    public function actionIndex($key) {        $page = Page::model()->findByAttributes(array('key' => $key));	    $attr = Category::model()->getAllTree();//	    $file=Yii::app()->request->getHostInfo()."/file.cache";//	    $attr = unserialize(file_get_contents($file));	    $catlogs =  Category::model()->getCatlog($attr,13);	    $sql = 'select category_id,title,`key` from page;';	    $command = Yii::app()->db->createCommand($sql);	    $rows = $command->queryAll();        $this->render('index', array(            'page' => $page,	        'catlogs' =>$catlogs,	        'rows' => $rows,        ));    }	// Uncomment the following methods and override them if needed    /*      public function filters()      {      // return the filter configuration for this controller, e.g.:      return array(      'inlineFilterName',      array(      'class'=>'path.to.FilterClass',      'propertyName'=>'propertyValue',      ),      );      } */      public function actions()      {      // return external action classes, e.g.:      return array(      'action1'=>'path.to.ActionClass',      'action2'=>array(      'class'=>'path.to.AnotherActionClass',      'propertyName'=>'propertyValue',      ),      );      }}